package com.demo.vo;import java.io.Serializable;/** * api返回信息封装类 *  * @author Administrator * */public class ResultUtil implements Serializable {	private static final long serialVersionUID = 1L;	public static <T> Result<T> success() {		return new Result<>(true);	}	public static <T> Result<T> success(T data) {		Result<T> result = new Result<>(true);		result.setData(data);		return result;	}	public static <T> Result<T> error(String errorMessage) {		Result<T> result = new Result<>(false);		result.setErrorMessage(errorMessage);		return result;	}	public static <T> Result<T> error(String errorCode, String errorMessage) {		Result<T> result = new Result<>(false);		result.setErrorCode(errorCode);		result.setErrorMessage(errorMessage);		return result;	}}